mapper.select:
        SELECT


--         级联表的字段
        ,canyin.canyin_name as canyinName
        ,canyin.canyin_uuid_number as canyinUuidNumber
        ,canyin.canyin_photo as canyinPhoto
        ,canyin.canyin_types as canyinTypes
        ,canyin.canyin_kucun_number as canyinKucunNumber
        ,canyin.canyin_price as canyinPrice
        ,canyin.canyin_old_money as canyinOldMoney
        ,canyin.canyin_new_money as canyinNewMoney
        ,canyin.canyin_clicknum as canyinClicknum
        ,canyin.canyin_content as canyinContent
        ,canyin.shangxia_types as shangxiaTypes
        ,canyin.canyin_delete as canyinDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes

        FROM canyin_liuyan  a
        left JOIN canyin canyin ON a.canyin_id = canyin.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.canyin_id = #{params.canyinId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.canyin_liuyan_text like CONCAT('%',#{params.canyinLiuyanText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.canyin_id IS NOT NULL


                and canyin.canyin_name like CONCAT('%',#{params.canyinName},'%')


                and canyin.canyin_uuid_number like CONCAT('%',#{params.canyinUuidNumber},'%')


                and canyin.canyin_types = #{params.canyinTypes}



                  and canyin.canyin_kucun_number >= #{params.canyinKucunNumberStart}   


                  and canyin.canyin_kucun_number <= #{params.canyinKucunNumberEnd}   


                and canyin.canyin_kucun_number = #{params.canyinKucunNumber}


                  and canyin.canyin_price >= #{params.canyinPriceStart}   


                  and canyin.canyin_price <= #{params.canyinPriceEnd}   


                and canyin.canyin_price = #{params.canyinPrice}


                  and canyin.canyin_old_money >= #{params.canyinOldMoneyStart}   


                  and canyin.canyin_old_money <= #{params.canyinOldMoneyEnd}   


                  and canyin.canyin_new_money >= #{params.canyinNewMoneyStart}   


                  and canyin.canyin_new_money <= #{params.canyinNewMoneyEnd}   


                  and canyin.canyin_clicknum >= #{params.canyinClicknumStart}   


                  and canyin.canyin_clicknum <= #{params.canyinClicknumEnd}   


                and canyin.canyin_clicknum = #{params.canyinClicknum}


                and canyin.canyin_content like CONCAT('%',#{params.canyinContent},'%')


                and canyin.shangxia_types = #{params.shangxiaTypes}



                  and canyin.canyin_delete >= #{params.canyinDeleteStart}   


                  and canyin.canyin_delete <= #{params.canyinDeleteEnd}   


                and canyin.canyin_delete = #{params.canyinDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        order by a.${params.orderBy} desc
